SELECT d.id, d.order_no, d.item_no AS itemNo, d.item_count AS itemCount, d.production_count AS productionCount, d.order_dtl_status AS orderDtlStatus, d.created_time, d.updated_time FROM t_order_dtl d WHERE d.order_no IN
#{ono}
ORDER BY d.order_no, d.created_time DESC

SELECT d.id, d.order_no AS orderNo, d.item_no AS itemNo, d.item_count AS itemCount, d.production_count AS productionCount, d.order_dtl_status AS orderDtlStatus, d.need_mid_count AS needMidCount, d.created_time AS createdTime, d.updated_time AS updatedTime, mis.bom_no AS bomNo, mis.item_name AS itemName, topMis.bom_no AS topBomNo FROM t_order_dtl d LEFT JOIN mes_item_stock mis ON d.item_no = mis.item_no LEFT JOIN t_production_order po ON po.sales_order_no = d.order_no LEFT JOIN mes_item_stock topMis ON topMis.item_no = po.item_no WHERE d.order_no IN
#{ono}
AND d.order_dtl_status = #{dto.orderDtlStatus}
AND d.order_no LIKE concat('%', #{dto.orderNo}, '%')
AND mis.item_name LIKE concat('%', #{dto.itemName}, '%')
ORDER BY d.order_no, d.created_time DESC
