mapper.select:
select tws.id,two.work_order_no,two.device_id,two.dept_id,tws.created_time,tws.created_By,two.procedure_name,tws.assign_count,two.plan_total_count,two.item_no from t_work_assign tws inner join t_work_order two on two.id=tws.work_order_id
and two.work_order_no like concat('%',#{p.workOrderNo},'%')
and two.device_id=#{p.deviceId}
and two.dept_id=#{p.deptId}
and two.item_no=#{p.itemNo}
and two.bom_no like concat('%',#{p.bomNo},'%')
and two.procedure_name in
#{name}
and tws.created_time >= STR_TO_DATE(concat(#{p.beginTime,jdbcType=VARCHAR}, ' 00:00:00'),'%Y-%m-%d %H:%i:%s')
and tws.created_time <= STR_TO_DATE(concat(#{p.endTime,jdbcType=VARCHAR}, ' 23:59:59'),'%Y-%m-%d %H:%i:%s')
order by tws.created_time desc
