mapper.select:
select two.id, two.alloc_id, two.order_dtl_id, two.work_order_no, two.item_no as itemNo, mis.item_name as itemName, tod.order_no as orderNo, two.procedure_code, two.procedure_name, two.plan_total_count, two.assign_count, two.real_count, two.to_review_real_count, two.deff_count, two.to_review_deff_count, two.device_id, two.dept_id, two.hours_fixed, two.shift_type, two.proc_status, two.data_status, two.updated_by, two.updated_time, two.created_by, two.created_time, two.state, two.group_id from t_work_order two left join mes_item_stock mis on two.item_no = mis.item_no left join t_order_dtl tod on two.order_dtl_id = tod.id where 1=1
and two.work_order_no like concat('%',#{p.workOrderNo},'%')
and two.device_id=#{p.deviceId}
and two.dept_id=#{p.deptId}
and two.item_no=#{p.itemNo}
and two.bom_no like concat('%',#{p.bomNo},'%')
and two.plan_total_count != two.real_count
and two.plan_total_count = two.real_count
and two.procedure_name like concat('%',#{p.procedureName} ,'%' )
and two.procedure_name in
#{name}
and exists ( select 1 from t_group_dtl g where g.group_id = two.group_id and g.user_id = #{p.userId} )
and two.state = #{p.state}
order by two.created_time desc

mapper.update:
update t_confirm_produce
set realCount = #{realCount},satus = '1',trayid = #{trayid},location = #{location}
where satus = '0' and deviceName = #{deviceName}

    and bomNo like concat('%',#{bomNo},'%')
